sql.where:
bpm.isMain=1
AND bpm.subject  LIKE #{subject}  
AND type.typeName LIKE #{typeName} 
AND type.nodePath LIKE #{nodePath} 
AND bpm.defKey  LIKE #{defKey}  
AND bpm.taskNameRule  LIKE #{taskNameRule}  
AND bpm.descp  LIKE #{descp}  
AND bpm.createtime  >=#{createtime,jdbcType=DATE} 
AND bpm.createtime <=#{endcreatetime,jdbcType=DATE} 
AND bpm.status  =#{status} 
AND bpm.isUseOutForm  =#{isUseOutForm} 
AND bpm.typeId  =#{typeId} 
AND bpm.disableStatus  =#{disableStatus} 
AND bpm.versionNo =#{versionNo}

mapper.select:
select bpm.*,type.tbl_typename typeName from BPM_DEFINITION bpm left join BPM_TYPE type on bpm.typeId=type.id
order by ${orderField} ${orderSeq}
order by defId desc

select def.*,type.tbl_typename typeName from BPM_DEFINITION def left join BPM_TYPE type on def.typeId=type.id where 1=1
order by def.${orderField} ${orderSeq}
order by def.defId desc

select a.*,b.typeName from BPM_DEFINITION a,sys_gl_type b
where a.typeId=b.typeId and a.parentDefId=#{parentDefId}
order by a.versionNo desc

select a.*,b.tbl_typename typeName from BPM_DEFINITION a left join BPM_type b on a.typeId=b.id
where a.parentDefId=#{parentDefId} and a.isMain=#{isMain}
order by a.versionNo desc

mapper.sql:
and def.isMain=1
 AND def.subject  LIKE #{subject} 
 AND def.defKey  LIKE #{defKey}  
 AND def.taskNameRule  LIKE #{taskNameRule}  
 AND def.descp  LIKE #{descp}  
 AND def.createtime  >=#{createtime,jdbcType=DATE} 
 AND def.createtime <=#{endcreatetime,jdbcType=DATE} 
 AND def.status  =#{status} 
 AND def.disableStatus  =#{disableStatus} 
 AND def.versionNo =#{versionNo}

mapper.select:
		select distinct 
		def.defId,def.typeId,def.subject,def.defKey,def.descp,
		def.status,def.actDeployId,def.actDefKey,def.actDefId,def.createtime,def.updatetime,
		def.createBy,def.updateBy,def.reason,def.versionNo,def.parentDefId,def.isMain,def.toFirstNode,def.directstart
		type.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE type on type.typeId=def.typeId
 		where (
			def.defKey in(
				select distinct rig.defKey from BPM_DEF_RIGHTS rig where rig.searchType=0
				and (
					(rig.ownerId = #{userId} and rig.rightType=1)
		 			or (rig.ownerId in (${roleIds}) and rig.rightType=2)
					or (rig.ownerId in (${orgIds}) and rig.rightType=3)
				)
			)
			or def.typeId in (
				select distinct rs.flowTypeId from BPM_DEF_RIGHTS rs where rs.searchType=1 and ((rs.ownerId = #{userId} and rs.rightType=1)
	 			or (rs.ownerId in (${roleIds}) and rs.rightType=2)
				or (rs.ownerId in (${orgIds}) and rs.rightType=3)
				)
			)

		)

			and type.nodePath like #{nodePath} 



		order by ${orderField} ${orderSeq}


		order by defId desc

select def.*,type.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE type on(type.typeId=def.typeId)
,BPM_DEF_RIGHTS rig where 1=1 and rig.searchType=0 and def.defKey=rig.defKey and rig.ownerId = #{userId}
and type.nodePath like #{nodePath}
union all select def.*,type.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE type on(type.typeId=def.typeId)
,BPM_DEF_RIGHTS rig , SYS_USER_ROLE role where 1=1 and rig.searchType=0 and def.defKey=rig.defKey and rig.ownerId= role.roleId and role.userId=#{userId}
and type.nodePath like #{nodePath}
union all select def.*,type.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE type on(type.typeId=def.typeId)
,BPM_DEF_RIGHTS rig ,SYS_USER_ORG org where 1=1 and rig.searchType=0 and def.defKey=rig.defKey and rig.ownerId =org.orgId and org.userId=#{userId}
and type.nodePath like #{nodePath}
union all select def.*,ty.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE ty on(ty.typeId=def.typeId) ,BPM_DEF_RIGHTS rig where 1=1 and rig.searchType=1 and rig.flowtypeid=ty.typeId
and ty.nodePath like #{nodePath}
and rig.ownerId =#{userId}
union all select def.*,ty.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE ty on(ty.typeId=def.typeId) ,BPM_DEF_RIGHTS rig, SYS_USER_ROLE role where 1=1 and rig.searchType=1 and rig.flowtypeid=ty.typeId
and rig.ownerId= role.roleId
and ty.nodePath like #{nodePath}
and role.userId=#{userId}
union all select def.*,ty.typeName typeName from BPM_DEFINITION def left join SYS_GL_TYPE ty on(ty.typeId=def.typeId) ,BPM_DEF_RIGHTS rig, SYS_USER_ORG org where 1=1 and rig.searchType=1 and rig.flowtypeid=ty.typeId
and rig.ownerId =org.orgId
and ty.nodePath like #{nodePath}
and org.userId=#{userId}

mapper.update:
UPDATE BPM_DEFINITION SET 
disableStatus=#{disableStatus} 
where defid=#{defId}
